VAR BtnURL = [SeasonWinnerLink]
RETURN
"<div style='text-align:center;'>
<style>
.watch-btn {
    display:inline-flex;
    align-items:center;
    gap:8px;
    padding:6px 14px;
    border-radius:10px;
    text-decoration:none;
    color:#fff;
    font-family:Arial,sans-serif;
    font-weight:600;
    font-size:14px;
    background:linear-gradient(90deg,#1797FF 0%,#7A4BFF 55%,#FF2A8A 100%);
    box-shadow:0 5px 15px rgba(17,135,255,0.18);
    transition:transform 150ms ease, box-shadow 150ms ease, filter 150ms ease, color 150ms ease;
}
.watch-btn__icon {
    display:inline-flex;
    align-items:center;
    justify-content:center;
    width:26px;
    height:26px;
    border-radius:999px;
    background:rgba(255,255,255,0.12);
}
.watch-btn__icon svg {
    display:block;
    transition:fill 150ms ease;
}
.watch-btn:hover {
    transform:translateY(-2px);
    box-shadow:0 10px 20px rgba(17,135,255,0.2);
    filter:saturate(1.02);
    color:black;
}
.watch-btn:hover .watch-btn__icon svg {
    fill:black;
}
</style>
<a class='watch-btn' href='" & BtnURL & "' target='_blank'>
    <span class='watch-btn__icon'>
        <svg viewBox='0 0 24 24' width='14' height='14' fill='white' xmlns='http://www.w3.org/2000/svg'>
            <path d='M8 5v14l11-7L8 5z'/>
        </svg>
    </span>
    <span>Watch Highlights</span>
</a>
</div>"
